import { defineComponent, ref, reactive } from "vue";
import { Todo } from "@/types/todo";
export default defineComponent({
  name: "Head",
  // props: {
  //   addToPlan: {
  //     type: Function,
  //     required: true,
  //   },
  // },
  setup(props, { emit }) {
    const todoItem = ref("");
    const add = () => {
      const text = todoItem.value;
      if (!text.trim()) return;
      const todo: Todo = {
        id: Date.now(),
        title: text.trim(),
        isCompleted: false,
      };
      // // setup中不能直接调用props中的内容
      // // 因此此处用到setup的props参数
      emit("addToPlan", todo);
      // props.addToPlan(todo);
      todoItem.value = "";
    };
    return {
      todoItem,
      add,
    };
  },
});
