import { defineComponent, ref, reactive } from "vue";
export default defineComponent({
  name: "name",
  setup() {
    // ref和reactive是vue3的composition api中2个最终要的响应式api
    const m1 = ref("bac");
    const m2 = reactive({
      x: 1,
      y: {
        z: "abc",
      },
    });
    const m3 = ref({
      a1: 2,
      a2: {
        a3: "abc",
      },
    });
    console.log(m1, m2, m3);
    const update = () => {
      console.log("xxx");
      m1.value += "m1";
      m2.x = 3;
      m3.value.a1 = 5;
    };
    return {
      m1,
      m2,
      m3,
      update,
    };
  },
});
